import React, { useState } from 'react';
import Modal from 'react-modal';

// Set app element for accessibility reasons
Modal.setAppElement('#root');

function ImageViewer({ src, alt = '', description = '' }) {
    const [isOpen, setIsOpen] = useState(false);

    // Function to open the modal
    const openModal = () => {
        setIsOpen(true);
    };

    // Function to close the modal
    const closeModal = () => {
        setIsOpen(false);
    };

    return (
        <div>
            {/* Image component */}
            <img
                src={src}
                alt={alt}
                loading="lazy"
                className="h-100 w-full object-cover object-center transition duration-200 group-hover:scale-110 cursor-pointer"
                onClick={openModal} // Open modal when the image is clicked
            />

            {/* Modal component */}
            <Modal
                isOpen={isOpen}
                onRequestClose={closeModal}
                contentLabel="Image Modal"
                style={{
                    overlay: {
                        backgroundColor: 'rgba(0, 0, 0, 0.75)',
                    },
                    content: {
                        top: '50%',
                        left: '50%',
                        right: 'auto',
                        bottom: 'auto',
                        marginRight: '-50%',
                        transform: 'translate(-50%, -50%)',
                        display: 'flex', // Set display to flex to align content horizontally
                        flexDirection: 'row', // Ensure content is in a row (horizontal alignment)
                        width: '80vw',
                        maxWidth: '90%',
                        maxHeight: '80vh',
                        overflow: 'auto',
                        padding: '20px',
                    },
                }}
            >
                {/* Close button */}
                <button
                    onClick={closeModal}
                    className="close-button text-xl absolute top-2 right-2"
                >
                    Ã—
                </button>

                {/* Container to hold the image and description */}
                <div style={{ display: 'flex', flexDirection: 'row', width: '100%', height: '100%' }}>
                    {/* Container for the image */}
                    <div style={{ flex: 3, marginRight: '20px' }}>
                        <img
                            src={src}
                            alt={alt}
                            className="w-full h-full object-cover transition duration-200"
                        />
                    </div>

                    {/* Container for the description */}
                    <div style={{ flex: 1 }}>
                        {/* Add the description text */}
                        <div className="description-text">
                            <p>{description}</p>
                        </div>
                    </div>
                </div>
            </Modal>
        </div>
    );
}

export default ImageViewer;
