import React, { useState } from "react";

const Modal = ({ isOpen, onClose, imageSrc, description, downloadUrl }) => {
  const [isDownloading, setIsDownloading] = useState(false);

  const handleDownload = () => {
    // Add your logic to initiate download
    setIsDownloading(true);
    // Example: window.location.href = downloadUrl;
  };

  return (
    <div className={`modal ${isOpen ? "is-open" : ""}`}>
      <div className="modal-content">
        <span className="close" onClick={onClose}>
          &times;
        </span>
        <img src={imageSrc} alt="Project" className="modal-image" />
        <p>{description}</p>
        <button onClick={handleDownload}>
          {isDownloading ? "Downloading..." : "Download"}
        </button>
      </div>
    </div>
  );
};

const DescModal = ({ imageSrc, downloadUrl, description }) => {
  const [modalOpen, setModalOpen] = useState(false);

  const handleOpenModal = () => {
    setModalOpen(true);
  };

  const handleCloseModal = () => {
    setModalOpen(false);
  };

  return (
    <div>
      <a
        href={downloadUrl} // Or any other URL you want to use
        className="group h-48 overflow-hidden rounded-lg shadow-lg md:col-span-2 md:h-80"
        onClick={handleOpenModal}
      >
        <img
          src={imageSrc}
          loading="lazy"
          alt=""
          className="h-full w-full object-cover object-center transition duration-200 group-hover:scale-110"
        />
      </a>
      <Modal
        isOpen={modalOpen}
        onClose={handleCloseModal}
        imageSrc={imageSrc}
        description={description}
        downloadUrl={downloadUrl}
      />
    </div>
  );
};

export default DescModal;
