import React, { useState } from "react";
import PropTypes from "prop-types"; // Import PropTypes for type checking

const CardDescL = ({ titlee, imagee, descriptionn }) => {
  const [isFlipped, setIsFlipped] = useState(false);

  const handleFlip = () => {
    setIsFlipped(!isFlipped);
  };

  return (
    <div
      className={`card-containerr ${isFlipped ? "is-flipped" : ""}`}
      onClick={handleFlip}
    >
      <div className="cardd">
        <div className="front">
          <img src={imagee} alt="" className="imagee" />
          <h3 className="titlee">{titlee}</h3>
        </div>
        <div className="back">
          <p className="descriptionn">{descriptionn}</p>
        </div>
      </div>
    </div>
  );
};

// Define PropTypes for type checking
CardDescL.propTypes = {
  titlee: PropTypes.string.isRequired,
  imagee: PropTypes.string.isRequired,
  descriptionn: PropTypes.string.isRequired,
};

export default CardDescL;
