import React, { useState } from "react";
import Modal from "react-modal";
const DescModal = ({ imageSrc, downloadUrl, description }) => {
  const [modalOpen, setModalOpen] = useState(false);

  const handleOpenModal = () => {
    setModalOpen(true);
  };

  const handleCloseModal = () => {
    setModalOpen(false);
  };

  const handleDownload = () => {
    // Add your logic to initiate download
    window.location.href = downloadUrl;
  };

  return (
    <div className="relative">
      <a
        href="#"
        onClick={(e) => {
          e.preventDefault();
          handleOpenModal();
        }}
      >
        <img
          src={imageSrc}
          loading="lazy"
          alt=""
          className="h-full w-full object-cover object-center transition duration-200 group-hover:scale-110"
        />
        <div className="absolute inset-0 flex items-center justify-center bg-black bg-opacity-50 hover:bg-opacity-25 transition-opacity opacity-0 hover:opacity-100">
          <span className="text-white text-lg">{description}</span>
        </div>
      </a>
      <Modal isOpen={modalOpen} onClose={handleCloseModal}>
        <div className="modal-content">
          <span className="close" onClick={handleCloseModal}>
            &times;
          </span>

          <p>{description}</p>
          <button onClick={handleDownload}>Download</button>
        </div>
      </Modal>
    </div>
  );
};

export default DescModal;
