import React, { useState } from "react";
import Modal from "react-modal";
import { FaRegArrowAltCircleRight } from "react-icons/fa";

const DescModal = ({ imageSrc, downloadUrl, description }) => {
  const [modalOpen, setModalOpen] = useState(false);

  const handleOpenModal = () => {
    setModalOpen(true);
  };

  const handleCloseModal = () => {
    setModalOpen(false);
  };

  const handleDownload = () => {
    // Check if the downloadUrl starts with 'http' or 'https'
    if (downloadUrl.startsWith("http")) {
      // If it starts with 'http' or 'https', open it in a new tab
      window.open(downloadUrl, "_blank");
    } else {
      // Otherwise, assume it's a direct download link
      // Add your logic to initiate download
      window.location.href = downloadUrl;
    }
  };
  return (
    <div>
      <div
        className="fixed inset-0 z-50 bg-black bg-opacity-50"
        style={{ display: modalOpen ? "block" : "none" }}
      ></div>
      <div
        className={`fixed z-50 top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 bg-gray-800 rounded-lg p-8 ${
          modalOpen ? "block" : "hidden"
        }`}
        style={{ width: "700px", maxWidth: "90%" }}
      >
        <button
          className="absolute top-4 right-4 text-white text-2xl"
          onClick={handleCloseModal}
        >
          &times;
        </button>
        <span className="mt-4 mx-5 flex relative text-3xl">Description</span>
        <p
          className="mt-3 mx-5 text-white pr-4 overflow-auto"
          style={{ maxHeight: "300px" }}
        >
          {description}
        </p>

        <button
          className="flex justify-center items-center w-40 gap-2 duration-100 transition-all bottom-4 left-1/2 transform -translate-x-1/2 mb-6 border hover:opacity-80  border-gray-200 rounded-md py-2 bg-neutral-300"
          onClick={handleDownload}
        >
          <span className="text-xl mb-1 text-violet-700">Explore</span>
          <FaRegArrowAltCircleRight className="text-violet-700" fontSize={25} />
        </button>
      </div>
    </div>
  );
};

export default DescModal;
