import React, { useState } from "react";
import Modal from "react-modal";

const DescModal = ({ imageSrc, downloadUrl, description }) => {
  const [modalOpen, setModalOpen] = useState(false);

  const handleOpenModal = () => {
    setModalOpen(true);
  };

  const handleCloseModal = () => {
    setModalOpen(false);
  };

  const handleDownload = () => {
    // Add your logic to initiate download
    window.location.href = downloadUrl;
  };

  return (
    <div className="relative">
      <a
        href={downloadUrl} // or any other URL you want to use
        target="_blank" // open the link in a new tab
        rel="noopener noreferrer" // recommended for security reasons
        onClick={(e) => {
          e.preventDefault();
          handleOpenModal();
        }}
      >
        <img
          src={imageSrc}
          loading="lazy"
          alt=""
          className="h-full w-full object-cover object-center transition duration-200 group-hover:scale-110"
        />
        <div className="absolute inset-0 flex items-center justify-center bg-black bg-opacity-50 hover:bg-opacity-25 transition-opacity opacity-0 hover:opacity-100">
          <span className="text-white text-lg">{description}</span>
        </div>
      </a>
      <Modal
        isOpen={modalOpen}
        onRequestClose={handleCloseModal}
        style={{
          content: {
            maxWidth: "500px",
            maxHeight: "500px",
            margin: "auto",
          },
        }}
      >
        <div className="modal-content">
          <button className="close" onClick={handleCloseModal}>
            &times;
          </button>
          <p>{description}</p>
          <button
            className="flex justify-center w-40 mb-10 border text-violet border-gray-200 rounded-md px-4 py-2 bg-gray-900"
            onClick={handleDownload}
          >
            Download
          </button>
        </div>
      </Modal>
    </div>
  );
};

export default DescModal;
