import React, { useState } from "react";
import proj1 from "../assets/p1.png";
import proj2 from "../assets/macoy.png";
import About from "./About";
import GalleryViewer from "./modal/GalleryViewer";

const Galle = () => {
  const [galleryImages, setGalleryImages] = useState([proj1, proj2]);

  const handleUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.onload = () => {
      setGalleryImages([...galleryImages, reader.result]);
    };
    reader.readAsDataURL(file);
  };

  return (
    <div className="py-6 max-w-[1200px] mx-auto">
      <div className="mb-4 flex items-center justify-between">
        <div className="flex flex-col gap-4">
          <h2 className="text-2xl lg:text-3xl text-white">
            My
            <span> Gallery</span>
          </h2>
          <p className="text-gray-500">This is my Gallery.</p>
        </div>
        <div>
          <label htmlFor="upload" className="cursor-pointer">
            Upload
            <input
              id="upload"
              type="file"
              accept="image/*"
              className="hidden"
              onChange={handleUpload}
            />
          </label>
        </div>
      </div>

      <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-3 lg:grid-cols-4 gap-1 sm:gap-1 lg:gap-1">
        {galleryImages.map((image, index) => (
          <div
            key={index}
            className="group h-48 w-48 md:h-64 md:w-64 overflow-hidden rounded-lg shadow-lg"
          >
            <GalleryViewer src={image} alt={`Project ${index + 1} image`} />
          </div>
        ))}
      </div>

      <div className="border-b border-gray-400 py-10"></div>
      <About />
    </div>
  );
};

export default Galle;
